package practiceCode;

public class Half_pyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int row=4;
		System.out.println("Half Pyramid");
		for(int i=1; i<=4; i++)
		{
			for(int j=1;j<=i;j++)
			{
				System.out.print("* ");
			}
			System.out.println();
		}	
	}
}
